void PortE_Init1(void){unsigned long delay;
	SYSCTL_RCGC2_R |= 0x00000010; //used to be SYSCTL_RCGCGPIO_R
	delay = SYSCTL_RCGC2_R;
	GPIO_PORTE_DIR_R &= ~0x0000003D; //pe0, pe2-pe5 are movement/attack buttons
	GPIO_PORTE_AFSEL_R &= 0xFFFFFFFF;
	GPIO_PORTE_DEN_R |= 0x0000003D; 
}


uint32_t DisplayTitle(void){
	//draw background before coming to this function
	//inputs: none
	//outputs: 1 once the player presses a key
	uint32_t count;
	uint16_t black = ST7735_Color565(0, 0, 0);
	uint16_t grey = ST7735_Color565(150, 150, 150);
	char prompt1[20]={'P', 'r', 'e', 's', 's', ' ', 'a', 'n', 'y', ' ', 'b', 'u', 't', 't', 'o', 'n', ' ', 't', 'o', 0x00};
	char prompt2[] = {'s', 't', 'a', 'r', 't', 0x00};
	PortE_Init1();
	ST7735_FillScreen(0xEEF8);
	ST7735_DrawBitmap(39, 120, &pokeball[0], 48, 48);//ST7735_DrawBitmap(59, 52, const &pokeball[0], 48, 48)
	ST7735_DrawChar(9, 30, 0x50, black, 0xEEF8, 2);//P, adjust 9 so that POKKEN 2D appears in middle of screen, subsequent x values should be increased by 12, 30 should be 24 for all drawchars
	ST7735_DrawChar(21, 30, 0x4F, black, 0xEEF8, 2);//O,
	ST7735_DrawChar(33, 30, 0x4B, black, 0xEEF8, 2);//K, 
	ST7735_DrawChar(45, 30, 0x4B, black, 0xEEF8, 2);//K,
	ST7735_DrawChar(57, 30, 0x45, black, 0xEEF8, 2);//E, 
	ST7735_DrawChar(69, 30, 0x4E, black, 0xEEF8, 2);//N
	
	ST7735_DrawChar(93, 30, 0x32, black, 0xEEF8, 2);//2, 93 should be previous drawchar's x value plus 24 
	ST7735_DrawChar(105, 30, 0x44, black, 0xEEF8, 2);//D
	
	while(GPIO_PORTE_DATA_R==0){
		count = ST7735_DrawString(0, 14, &prompt1[0], black);
		ST7735_FillRect(0, 120, 128, 40, 0xEEF8);//120 should be 96, 128 should be 160, 40 should be 32
	}
	return 1;
}